mapper.insert:
  		INSERT INTO ANIMAL_TRAINING
			(
			    TRAINING_NO
			    , NO
			    , TITLE
			    , CONTENT
			)
			VALUES
			(
			    SEQ_ANIMAL_TRAINING_NO.NEXTVAL
			    ,#{animalNo}
			    ,#{trainingTitle}
			    ,#{trainingContent}
			)

  		INSERT INTO ANIMAL_HEALTH_SCREENING
			(
			    NO
			    ,ANIMAL_NO
			    ,LIFE_STYLE_MANAGEMENT
			    ,DISEASE
			    ,STATE_OF_HEALTH
			)
			VALUES
			(
			    SEQ_ANIMAL_HEALTH_SCREENING_NO.NEXTVAL
			    ,#{animalNo}
			    ,#{lifeStyleManagement}
			    ,#{stateOfHealth}
			    ,#{disease}
			)

mapper.select:
SELECT A.NO AS ANIMAL_NO ,AHS.NO AS NO ,AHS.STATE_OF_HEALTH ,TO_CHAR(AHS.CHECKUP_DATE , 'YYYY-MM-DD') AS CHECKUP_DATE ,A.NICK_NAME FROM ANIMAL_HEALTH_SCREENING AHS JOIN ANIMAL A ON AHS.ANIMAL_NO = A.NO
WHERE ANIMAL_NO = #{no}
WHERE NO = #{healthNo}
AND A.STATUS = 'O'
