mapper.select:

SELECT FORM
FROM APPROVAL_CATEGORY
WHERE NO = #{category}

mapper.insert:
INSERT INTO APPROVER ( NO, APPROVAL_NO, APPROVAL_STATE, APPROVER_NO, NEXT_APPROVAER_NO, APPROVAL_DATE ) SELECT SEQ_APPROVER_NO.NEXTVAL, TEMP.* FROM (
SELECT #{item.approvalNo} AS APPROVAL_NO, 0, #{item.approverNo} AS APPROVER_NO, #{item.nextApprovaerNo} AS NEXT_APPROVAER_NO, NULL FROM DUAL
) TEMP

mapper.select:

SELECT CATEGORY_NO
FROM ELECTRONIC_APPROVAL
WHERE NO = #{no}

mapper.update:

UPDATE APPROVER
SET APPROVAL_STATE = #{decisionNo},
    APPROVAL_DATE = SYSDATE
WHERE APPROVER_NO = #{userNo}
	AND APPROVAL_NO = #{no}


UPDATE LETTER_OF_APPROVAL
SET INSTRUCTION = #{instruction}
WHERE APPROVAL_NO = #{no}

mapper.select:

SELECT COUNT(NO)
FROM APPROVER
WHERE APPROVAL_NO = #{approvalNo}


SELECT COUNT(NO)
FROM APPROVER
WHERE APPROVER_NO = #{userNo}


SELECT COUNT(NO)
FROM APPROVAL_CC
WHERE CC_NO = #{userNo}


SELECT COUNT(NO)
FROM ELECTRONIC_APPROVAL
WHERE DRAFTER = #{userNo}
