mapper.select:

SELECT FORM
FROM APPROVAL_CATEGORY
WHERE NO = #{category}

mapper.insert:
INSERT INTO APPROVER ( NO, APPROVAL_NO, APPROVAL_STATE, APPROVER_NO, NEXT_APPROVAER_NO, APPROVAL_DATE ) SELECT SEQ_APPROVER_NO.NEXTVAL, TEMP.* FROM (
SELECT #{item.approvalNo} AS APPROVAL_NO, 0, #{item.approverNo} AS APPROVER_NO, #{item.nextApprovaerNo} AS NEXT_APPROVAER_NO, SYSDATE FROM DUAL
) TEMP
