mapper.insert:
INSERT INTO MAIL_ATTACHMENT ( NO, MAIL_NO, ORIGIN_NAME, CHANGE_NAME ) SELECT SEQ_MAIL_ATTACHMENT_NO.NEXTVAL, TEMP.* FROM (
SELECT #{item.mailNo} AS MAIL_NO, #{item.originName} AS ORIGIN_NAME, #{item.changeName} AS CHANGE_NAME FROM DUAL
) TEMP

insert.foreach:
INTO MAIL_CC ( MAIL_NO, CC_NO, BCC_CHECK ) VALUES ( #{item.mailNo}, (SELECT NO FROM "USER" WHERE MAIL = #{item.ccNo}),
default #{item.bccCheck}
)

mapper.select:

SELECT NO, MAIL_NO, ORIGIN_NAME, CHANGE_NAME
FROM MAIL_ATTACHMENT
WHERE MAIL_NO = #{no}


SELECT NO, USER_NO, NAME
FROM MAILBOX
WHERE USER_NO = #{no}

mapper.update:

UPDATE MAIL_RECIPIENT
SET MAILBOX_NO = NULL
WHERE MAILBOX_NO = #{no}
