mapper.update:
UPDATE MEETINGROOM
SET STATUS = 'X'
WHERE NO = #{no}

UPDATE MEETINGROOM
SET STATUS = 'S'
WHERE NO = #{no}

UPDATE MEETINGROOM
SET STATUS = 'O'
WHERE NO = #{no}

mapper.select:
SELECT COUNT(*)
FROM MEETINGROOM_RESERVATION
WHERE MEETINGROOM_NO = #{meetingroomNo}
AND "DATE" = #{date}
AND START_TIME = #{startTime}
AND STATUS = 0

SELECT COUNT(*)
FROM MEETINGROOM_RESERVATION
WHERE MEETINGROOM_NO = #{meetingroomNo}
AND "DATE" = #{date}
AND START_TIME = #{startTime}
AND STATUS = 0

mapper.update:
UPDATE MEETINGROOM_RESERVATION
SET STATUS = 1
WHERE NO = #{resNo}
