mapper.update:
UPDATE NOTICE
SET HIT = HIT + 1
WHERE NO = ${no}

UPDATE NOTICE
TITLE = #{title}, CONTENT = #{content}, ENROLL_DATE = #{enrollDate}, STATUS = #{status}, ORIGIN_NAME = #{originName}, CHANGE_NAME = #{changeName}, RIGHTCLICK_YN = #{rightclickYn}, COMMENT_YN = #{commentYn}, BOARD_LIMIT = #{boardLimit}, HIT = #{hit},
WHERE NO = #{no}

mapper.insert:
INSERT INTO NOTICE
(
	NO
	, ADMIN_NO
	, CATEGORY_NO
	, TITLE
	, CONTENT
	, CHANGE_NAME
	, RIGHTCLICK_YN
	, COMMENT_YN
	, BOARD_LIMIT
	, HIT
)
VALUES
(
	SEQ_NOTICE_NO.NEXTVAL
	, 1
	, 1
	, #{title}
	, #{content}
	, #{changeName}
	, #{rightclickYn}
	, #{commentYn}
	, #{boardLimit}
	, 0
)
