mapper.update:
UPDATE "USER"
DEPARTMENT_NO=#{departmentNo}, RANK_NO=#{rankNo}, POSITION_NO=#{positionNo}, STATE_NO=#{stateNo}, AGENT=#{agent}, DIVISION_NO=#{divisionNo}, ID=#{id}, PWD=#{pwd}, NAME=#{name}, GENDER=#{gender}, BIRTH_DATE=#{birthDate}, PROFILE=#{profile}, LINE_INTRO=#{lineIntro}, ADDRESS=#{address}, PHONE=#{phone}, MAIL=#{mail}, REGISTRATION_DATE=#{registrationDate}, OFFICE_NUMBER=#{officeNumber}, LEAVE_COUNT=#{leaveCount}, LEAVE_DATE=#{leaveDate}, LEAVE_REASON=#{leaveReason},
WHERE NO = #{no}
